// Recursive function to find longest decreasing subsequence from index x
// with previous element being less than `last`
ll sol(ll x, ll last) {
    if (x < 0) return 0;

    pair<ll, ll> state = {x, last};
    if (dp.count(state)) return dp[state];
    it++;

    // Option 1: Skip current coin
    ll res = sol(x - 1, last);

    // Option 2: Take current coin if it < last
    if (coins[x] < last) {
        res = max(res, 1 + sol(x - 1, coins[x]));
    }

    return dp[state] = res;
}

